mapper.select:
select a.productCode, a.productName, a.barcode, a.imageCode, a.qrCode, a.price, ifnull(a.discount,0) as discount, a.productUrl, ifnull(a.`desc`,'') as `desc`, a.updateYN, a.cateLCode, a.cateMCode, a.cateSCode, DATE_FORMAT(A.regdate, '%Y-%m-%d' ) AS regdate, a.placeCode, a.companyCode, ifnull(DATE_FORMAT(A.saleStart, '%Y-%m-%d' ),'') AS saleStart, ifnull(DATE_FORMAT(A.saleEnd, '%Y-%m-%d' ),'') AS saleEnd, a.saleType, ifnull(b.cnt,0) as cnt
from tbl_product a left outer join (select productCode,count(*) as cnt from tbl_product_mapping group by productCode) b on a.productCode = b.productCode
where 1=1
and cateLCode = #{cateL}
and cateMCode = #{cateM}
and cateSCode = #{cateS}
and productName like '%' #{productName} '%'

select b.* from tbl_product_mapping a
inner join tbl_tag b on a.tagCode = b.tagCode
where a.productCode = #{productCode}

mapper.update:
update tbl_product set productName = #{productName},
`desc`=#{desc},
price = #{price},
discount = #{discount, jdbcType=DOUBLE}, saleStart = #{saleStart, jdbcType=DATE}, saleEnd = #{saleStart, jdbcType=DATE}, `position` = #{position, jdbcType=SMALLINT},
saleType = #{bundle}
WHERE productCode = #{productCode}
