mapper.update:
update tbl_tag
set tagID = #{tagID}, `desc` = #{desc}, templateCode = #{templateCode}
WHERE
tagCode = #{tagCode}

INSERT INTO tbl_product_mapping(productCode,tagCode,regdate) values (#{productCode}, #{tagCode},now())
ON DUPLICATE KEY
UPDATE productCode = #{productCode},tagCode=#{tagCode}

mapper.select:
select count(*) as CountAll from tbl_tag where 1=1
and gatewayCode = #{gatewayCode}
and tagID like '%' #{tagID} '%'


select count(*) as batteryCount from tbl_tag  where batteryStat <=20


    and gatewayCode = #{gatewayCode}


    and tagID like '%' #{tagID} '%'


select count(*) as signalCount from tbl_tag  where signalStat <=20


    and gatewayCode = #{gatewayCode}


    and tagID like '%' #{tagID} '%'


select count(*) as callCount from tbl_tag g where isCall='Y'


    and gatewayCode = #{gatewayCode}


    and tagID like '%' #{tagID} '%'

mapper.update:
update tbl_tag
set isCall = #{isCall}
WHERE
tagCode = #{tagCode}
